class Vector {
  constructor(x = 0, y = 0, spring = 0, // 彈力細數
  friction = 1, // 摩擦力
  speed = 1) {
    // 移動位置
    this.moveX = 0;
    this.moveY = 0;
    this.x = x;
    this.y = y;
    this.originalX = x;
    this.originalY = y;
    this.transformX = x;
    this.transformY = y;
    this.spring = spring;
    this.friction = friction;
    this.speed = speed;
  }

  transform(scale, translateX = 0, translateY = 0) {
    this.x = this.originalX * scale + translateX;
    this.y = this.originalY * scale + translateY;
    this.transformX = this.x;
    this.transformY = this.y;
  }

  setPos(pos) {
    this.x = pos.x;
    this.y = pos.y;
    this.originalX = pos.x;
    this.originalY = pos.y;
  }

  moveTo(pos) {
    this.fnX = pos.x;
    this.fnY = pos.y;

    if (this.spring > 0) {
      this.moveX += (this.fnX - this.x) * this.spring;
      this.moveY += (this.fnY - this.y) * this.spring;
      this.moveX *= this.friction;
      this.moveY *= this.friction;
      this.x += this.moveX * this.speed;
      this.y += this.moveY * this.speed;
    } else {
      this.x = this.fnX;
      this.y = this.fnY;
    }
  }

}

export { Vector };
